import React from 'react';

class Form extends React.Component {

  constructor() {
    super()

    this.state = {
      numeroPreferido: '',
      nome: '',
      idade: 0,
      desabafo: '',
    }

    this.handleChange = this.handleChange.bind(this)
  }

  handleChange({target}) {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    this.setState({
      [name]: value,
    })
  }

  render() {
    return(
      <form className="form">
        <label> Escolha seu numero preferido
          <select name="opcoes" value={this.state.numeroPreferido}>
            <option>01</option>
            <option>02</option>
            <option>03</option>
            <option>04</option>
          </select>
        </label>
        <label> Nome
          <input 
            type="text" 
            name="nome" 
            value={this.state.nome}
            onChange={this.handleChange}
          />
        </label>
        <label> Idade
          <input 
            type="number"
            name="idade"
            value={this.state.idade}
            onChange={this.handleChange}
          />
        </label>
        <textarea 
          name="desabafo"
          value={this.state.desabafo}
          onChange={this.handleChange}
        />
      </form>
    );
  }
}

export default Form;
