import React from 'react';

class Form extends React.Component {

  constructor() {
    super()

    this.state = {
      opcoes: '',
      nome: '',
      idade: 0,
      desabafo: '',
    }
  }

  render() {
    return(
      <form className="form">
        <select>
          <option>01</option>
          <option>02</option>
          <option>03</option>
          <option>04</option>
        </select>
        <input 
          type="text" 
          name="nome" 
        />
        <input 
          type="number"
          name="idade"
        />
        <textarea 
          name="desabafo"
        />
      </form>
    );
  }
}

export default Form;
