import React from 'react';

class Form extends React.Component {

  constructor() {
    super()

    this.state = {
      opcoes: '',
      nome: '',
      idade: 0,
      desabafo: '',
    }
  }

  render() {
    return(
      <form className="form">
        <label> Escolha seu numero preferido
          <select>
            <option>01</option>
            <option>02</option>
            <option>03</option>
            <option>04</option>
          </select>
        </label>
      <label> Nome
        <input 
          type="text" 
          name="nome" 
        />
      </label>
        <input 
          type="number"
          name="idade"
        />
        <textarea 
          name="desabafo"
        />
      </form>
    );
  }
}

export default Form;
